/* CSS3+ styles */ ui-tooltip-shadow /* Adds a shadows to your tooltips */ ui-tooltip-rounded /* Adds a rounded corner to your tooltips */ ui-tooltip-youtube /* Youtube style */ ui-tooltip-jtools /* jTools tooltip style */ ui-tooltip-cluetip /* ClueTip style */ ui-tooltip-tipped /* Tipped style */ ui-tooltip-tipsy /* Tipsy style */

edit.action:
if ($row['topic_first_post_id'] != $row['topic_last_post_id'])
{
	$last_post_preview_text = trim_topic_preview($row['last_post_preview_text'], $config['topic_preview_limit']);
}
else if (isset($last_post_preview_text))
{
	unset($last_post_preview_text); //Clear any last post text that may be left over from previous topic
}

edit.find:
$sql_join = ' LEFT JOIN ' . POSTS_TABLE . ' p ON (p.post_id = t.topic_first_post_id)';
$sql_select =  ', p.post_text AS first_post_preview_text';

$sql = 'SELECT t.* ' . $sql_select . '
	FROM ' . TOPICS_TABLE . ' t
	' . $sql_join . 'WHERE ' . $db->sql_in_set(' t.topic_id', array_keys($shadow_topic_list));
$result = $db->sql_query($sql);

edit.action:
$sql_join = ' LEFT JOIN ' . POSTS_TABLE . ' p ON (p.post_id = t.topic_first_post_id)';
$sql_select =  ', p.post_text AS first_post_preview_text';
$sql_join2 = ' LEFT JOIN ' . POSTS_TABLE . ' pl ON (pl.post_id = t.topic_last_post_id)';
$sql_select2 =  ', pl.post_text AS last_post_preview_text';

$sql = 'SELECT t.* ' . $sql_select . $sql_select2 . '
	FROM ' . TOPICS_TABLE . ' t
	' . $sql_join . $sql_join2 . '
	WHERE ' . $db->sql_in_set(' t.topic_id', array_keys($shadow_topic_list));
$result = $db->sql_query($sql);

if ($row['topic_first_post_id'] != $row['topic_last_post_id'])
{
	$last_post_preview_text = trim_topic_preview($row['last_post_preview_text'], $config['topic_preview_limit']);
}
else if (isset($last_post_preview_text))
{
	unset($last_post_preview_text); //Clear any last post text that may be left over from previous topic
}

